script:
frappe.ui.form.on('Nekretnine', {
    refresh: function(frm) {
        // Fetch options from 'Tip transakcije' and initialize the multiselect
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Osoba',
                fields: ['suvlasnici']
            },
            callback: function(r) {
                if (r.message) {
                    let options = r.message.map(d => d.name);
                    initializeMultiselect(frm, options);
                }
            }
        });
    }
});

function initializeMultiselect(frm, options) {
    // Create a multiselect input
    $(frm.fields_dict['suvlasnici'].wrapper).empty().append('<input type="text" id="multiselect-suvlasnici" class="form-control">');

    $('#multiselect-suvlasnici').select2({
        placeholder: "Odaberi suvlasnike",
        multiple: true,
        data: options.map(option => ({ id: option, text: option })),
        allowClear: true
    });

    // Set initial values if any
    if (frm.doc.suvlasnici) {
        $('#multiselect-transakcije').val(frm.doc.suvlasnici.split(',')).trigger('change');
    }

    // Update the form field when selections change
    $('#multiselect-suvlasnici').on('change', function() {
        const selectedValues = $(this).val();
        frm.set_value('suvlasnici', selectedValues ? selectedValues.join(',') : '');
    });
}
